mapper.select:
select t_activity_order.*,t_member.userId from t_activity_order inner join t_member on t_activity_order.memberId = t_member.memberId where 1=1
and t_activity_order.mobilePhoneNum like #{param.mobilePhoneNum}
and t_activity_order.activityId = #{param.activityId} order by t_activity_order.orderId desc

select sum(t_activity_order.joinMan) from t_activity_order where 
t_activity_order.payStatus = #{param.status}

	and date_format(createDate,'%Y-%m-%d') = #{param.dateTime}


	and t_activity_order.activityId = #{param.activityId}

  	select sum(totalMoney) from t_activity_order where t_activity_order.totalMoney != -1
	and t_activity_order.payStatus = 2

	   and t_activity_order.activityId = #{param.activityId}

select * from t_activity_order

left join t_order_discount
on t_order_discount.orderId = t_activity_order.orderId

where 1=1 

	and comm like  CONCAT('%', #{sourceContext}, '%')


    and payStatus = #{orderStatus}



    and createDate between #{startTime} and #{endTime}



    and mobilePhoneNum like  CONCAT('%',#{mobilePhoneNum},'%') 


    and memberId = #{memberId} 


  and t_order_discount.status = 4
  and (payStatus = 3 or payStatus = 4)

    and retreatDate between #{startTime} and #{endTime}


	order by t_activity_order.createDate desc

select sum(redPackage) from t_order_discount toc join t_activity_order t 
	on toc.orderId = t.orderId
	and #{startTime} < t.createDate and t.createDate < #{endTime}

  		select tm.memberId,tm.imgpath,tao.joinMan from t_activity_order tao join t_member tm
			on tao.memberId = tm.memberId
			and tao.activityId = #{activeId}
