mapper.select:
     select * from t_goods_order

     left join t_order_discount
 	 on t_order_discount.orderId = t_goods_order.orderId

 	 where 1=1 


	and comm like  CONCAT('%', #{sourceContext}, '%')


    and payStatus = #{orderStatus}



    and createDate between #{startTime} and #{endTime}



    and mobilePhoneNum like  CONCAT('%',#{mobilePhoneNum},'%') 


    and memberId = #{memberId} 



  and t_order_discount.status = 0
  and (payStatus = 3 or payStatus = 4)

    and retreatDate between #{startTime} and #{endTime}



order by t_goods_order.createDate desc

select sum(redPackage) from t_order_discount toc join t_goods_order t 
	on toc.orderId = t.orderId
	and #{startTime} < t.createDate and t.createDate < #{endTime}

  	select * from t_goods_order

inner join t_order_discount
inner join t_goods

on t_order_discount.orderId = t_goods_order.orderId
and t_goods.goodsId = t_goods_order.gId

where 1=1

and t_goods_order.payStatus in (5,7)


   	and t_goods_order.orderId = #{orderId}





and t_goods_order.gId = #{id}




  		and t_goods.gName like #{contextName}



  		 and t_goods_order.payStatus = #{orderStatus}



  	 and  t_order_discount.retreatDate between #{startTime} and #{endTime}


 order by t_order_discount.retreatDate asc

  	select count(1) from t_goods_order

inner join t_order_discount

on t_order_discount.orderId = t_goods_order.orderId

where t_goods_order.gId = #{param.goodsId}

and t_goods_order.payStatus = #{param.payStatus}


   and  DATE_FORMAT(t_order_discount.retreatDate,'%Y-%m-%d') = DATE_FORMAT(#{param.today},'%Y-%m-%d')
