mapper.select:
select t_member_coupon_ref.rowId, t_member.memberId, t_member_coupon_ref.couponMethod, discountAmount, t_member_coupon_ref.status, expirtionDate, couponType, maxMoney, startMoney from t_member_coupon_ref inner join t_member on t_member_coupon_ref.memberId = t_member.memberId and t_member_coupon_ref.`status` !=1
and t_member.memberId = #{param.memberId}
order by status asc
