mapper.select:
 select * from t_studio_consume_order_record

     left join t_order_discount
     on t_order_discount.orderId = t_studio_consume_order_record.orderId


where 1=1  



	and comm like  CONCAT('%', #{sourceContext}, '%')


    and payStatus = #{orderStatus}




    and createDate between #{startTime} and #{endTime}




    and mobilePhoneNum like  CONCAT('%',#{mobilePhoneNum},'%') 


    and memberId = #{memberId} 



  and t_order_discount.status = 1
  and (payStatus = 3 or payStatus = 4)

    and retreatDate between #{startTime} and #{endTime}



order by t_studio_consume_order_record.createDate desc

select sum(redPackage) from t_order_discount toc join t_studio_consume_order_record t 
	on toc.orderId = t.orderId
	and #{startTime} < t.createDate and t.createDate < #{endTime}

	 select * from t_studio_consume_order_record 
	   inner join t_studio
	  inner join t_order_discount
   on t_order_discount.orderId = t_studio_consume_order_record.orderId
 and t_studio.studioId = t_studio_consume_order_record.studioId

  where 1=1

  and t_studio_consume_order_record.payStatus in(5,7)


 	and t_studio_consume_order_record.orderId = #{orderId}





	 and t_studio_consume_order_record.studioId = #{id}



		and t_studio.studioName like #{contextName}



		 and t_studio_consume_order_record.payStatus = #{orderStatus}




	 and  t_order_discount.retreatDate between #{startTime} and #{endTime}


	order by t_order_discount.retreatDate asc

  	select count(1) from t_studio_consume_order_record 

  	inner join t_order_discount
    on t_studio_consume_order_record.orderId = t_order_discount.orderId

        where t_studio_consume_order_record.studioId = #{param.studioId}
		and t_studio_consume_order_record.payStatus = #{param.payStatus}


		   and  DATE_FORMAT(t_order_discount.retreatDate,'%Y-%m-%d') = DATE_FORMAT(#{param.today},'%Y-%m-%d')
