mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_apply (orderNum, amount, createDate, 
  payStatus, messageStatus, orderMemo, 
  applyStatus, status, appAccountId
  )
values (#{ordernum,jdbcType=VARCHAR}, #{amount,jdbcType=DOUBLE}, #{createdate,jdbcType=VARCHAR}, 
  #{paystatus,jdbcType=VARCHAR}, #{messagestatus,jdbcType=VARCHAR}, #{ordermemo,jdbcType=VARCHAR}, 
  #{applystatus,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{appaccountid,jdbcType=INTEGER}
  )

mapper.select:
     select * from t_apply

inner join t_apply_account
inner join t_supplier
inner join t_supplier_login

on t_apply.appAccountId = t_apply_account.rowdId
and t_apply_account.likeType = 4
and t_supplier.rowId = t_apply_account.likeId

and t_supplier_login.supplierLoginId = t_supplier.supplierLoginId



 	and  t_apply.rowId = #{param.id}



    and t_apply.createDate between #{param.startDate} and #{param.endDate}



  and t_supplier_login.loginMobilePhone like #{param.mobilePhoneNum}



  and t_apply.applyStatus = #{param.applyStatus}


 order by t_apply.createDate desc
